-- 將舊有foreign到的bom item清空值
UPDATE wiprocurement.bom_item SET parts_ctgy_1=NULL WHERE parts_ctgy_1 IN (SELECT id FROM wiprocurement.drop_down_item WHERE layout_name='bom_item' AND field_name ='parts_ctgy_1' );
UPDATE wiprocurement.bom_item SET parts_ctgy_2=NULL WHERE parts_ctgy_2 IN (SELECT id FROM wiprocurement.drop_down_item WHERE layout_name='bom_item' AND field_name ='parts_ctgy_2' );
UPDATE wiprocurement.bom_item SET material_spec=NULL WHERE material_spec IN (SELECT id FROM wiprocurement.drop_down_item WHERE layout_name='bom_item' AND field_name ='material_spec' );
UPDATE wiprocurement.bom_item SET material=NULL WHERE material IN (SELECT id FROM wiprocurement.drop_down_item WHERE layout_name='bom_item' AND field_name ='material' );
-- 刪除舊的資料
DELETE FROM wiprocurement.drop_down_item WHERE layout_name='bom_item' AND field_name IN ('parts_ctgy_1', 'parts_ctgy_2', 'material_spec', 'material');

