CREATE TABLE IF NOT EXISTS thermal_fan (
	id uuid NOT NULL DEFAULT uuid_generate_v1(),
	fan_size varchar(200) NOT NULL,
    baseline uuid NOT NULL DEFAULT uuid_generate_v1(),
	remark varchar(400) NULL,
	create_time timestamptz NOT null default now(),
	disable_time timestamptz null,
	CONSTRAINT thermal_fan_pk PRIMARY KEY (id),
	CONSTRAINT thermal_fan_un UNIQUE (fan_size)
);

