	CONSTRAINT thermal_fan_bearing_pk PRIMARY KEY (id),
	CONSTRAINT thermal_fan_bearing_un UNIQUE (bearing_name)
);

CREATE TABLE IF NOT EXISTS thermal_fan_fan_bearing (
	id uuid NOT NULL DEFAULT uuid_generate_v1(),
    fan_id uuid not null,
	bearing_id uuid not null,
	remark varchar(400) NULL,
	create_time timestamptz NOT null default now(),
	disable_time timestamptz null,
	CONSTRAINT thermal_fan_fan_bearing_pk PRIMARY KEY (id),
    CONSTRAINT thermal_fan_fan_bearing_fk FOREIGN KEY (fan_id) REFERENCES thermal_fan(id),
    CONSTRAINT thermal_fan_fan_bearing_fk1 FOREIGN KEY (bearing_id) REFERENCES thermal_fan_bearing(id),
	CONSTRAINT thermal_fan_fan_bearing_un UNIQUE (fan_id,bearing_id)
);

