CREATE TABLE IF NOT EXISTS diecut_material_spec (
	id uuid NOT NULL DEFAULT uuid_generate_v1(),
	material_spec_name varchar(200) not NULL,
    part_category_2_id uuid not null,
	remark varchar(400) NULL,
	create_time timestamptz NOT null default now(),
	disable_time timestamptz null,
	CONSTRAINT diecut_material_spec_pk PRIMARY KEY (id),
    CONSTRAINT diecut_material_spec_fk FOREIGN KEY (part_category_2_id) REFERENCES part_category_2(id),
	CONSTRAINT diecut_material_spec_un UNIQUE (part_category_2_id,material_spec_name)
);

CREATE TABLE IF NOT EXISTS diecut_material (
	id uuid NOT NULL DEFAULT uuid_generate_v1(),
	material_name varchar(200) not NULL,
    diecut_material_spec_id uuid not null,
	remark varchar(400) NULL,
	create_time timestamptz NOT null default now(),
	disable_time timestamptz null,
	CONSTRAINT diecut_material_pk PRIMARY KEY (id),
    CONSTRAINT diecut_material_fk FOREIGN KEY (diecut_material_spec_id) REFERENCES diecut_material_spec(id),
	CONSTRAINT diecut_material_un UNIQUE (material_name,diecut_material_spec_id)
);

CREATE TABLE IF NOT EXISTS diecut_material_size_adder (
	id uuid NOT NULL DEFAULT uuid_generate_v1(),    
	size_begin numeric NOT NULL,
	size_end numeric not NULL,
	remark varchar(400) NULL,
	create_time timestamptz NOT null default now(),
	disable_time timestamptz null,
	CONSTRAINT diecut_material_size_adder_pk PRIMARY KEY (id),
	CONSTRAINT diecut_material_size_adder_un_b UNIQUE (size_begin),
	CONSTRAINT diecut_material_size_adder_un_e UNIQUE (size_end)
);

CREATE TABLE IF NOT EXISTS diecut_release_paper (
	id uuid NOT NULL DEFAULT uuid_generate_v1(),
	paper_name varchar(200) not NULL,
    remark varchar(400) NULL,
	create_time timestamptz NOT null default now(),
	disable_time timestamptz null,
	CONSTRAINT diecut_release_paper_pk PRIMARY KEY (id),
    CONSTRAINT diecut_release_paper_un UNIQUE (paper_name)
);

CREATE TABLE IF NOT EXISTS diecut_type (
	id uuid NOT NULL DEFAULT uuid_generate_v1(),
	type_name varchar(200) not NULL,
    remark varchar(400) NULL,
	create_time timestamptz NOT null default now(),
	disable_time timestamptz null,
	CONSTRAINT diecut_type_pk PRIMARY KEY (id),
    CONSTRAINT diecut_type_un UNIQUE (type_name)
);

CREATE TABLE IF NOT EXISTS diecut_area_times (
	id uuid NOT NULL DEFAULT uuid_generate_v1(),
	area_size int not NULL,
    remark varchar(400) NULL,
	create_time timestamptz NOT null default now(),
	disable_time timestamptz null,
	CONSTRAINT diecut_area_times_pk PRIMARY KEY (id),
    CONSTRAINT diecut_area_times_un UNIQUE (area_size)
);

