SET search_path TO formula, public, wiprocurement;
drop table if exists cableffc_connector_vendor;
drop table if exists cableffc_connector_type;

CREATE TABLE IF NOT EXISTS cableffc_connector_type (
	id uuid NOT NULL DEFAULT uuid_generate_v1(),
	type_name varchar(200) not NULL,
	remark varchar(400) NULL,
	create_time timestamptz NOT null default now(),
	disable_time timestamptz null,
	CONSTRAINT cableffc_connector_type_pk PRIMARY KEY (id),
	CONSTRAINT cableffc_connector_type_un UNIQUE (type_name)
);

CREATE TABLE IF NOT EXISTS cableffc_connector_vendor (
	id uuid NOT NULL DEFAULT uuid_generate_v1(),
	vendor_name varchar(200) NULL,
	vendor_pn varchar(200) not NULL,
	connector_type_id uuid NOT NULL,
	unit_price uuid NOT NULL DEFAULT uuid_generate_v1(),
	process_time uuid NOT NULL DEFAULT uuid_generate_v1(),
	remark varchar(400) NULL,
	create_time timestamptz NOT null default now(),
	disable_time timestamptz null,
	CONSTRAINT cableffc_connector_vendor_pk PRIMARY KEY (id),
	CONSTRAINT cableffc_connector_vendor_fk FOREIGN KEY (connector_type_id) REFERENCES cableffc_connector_type(id),
	CONSTRAINT cableffc_connector_vendor_un UNIQUE (vendor_pn)
);

delete from formula.parameter_value as pv where activate_date_id in (select id from formula.schedule_date as sd where sd.formula_type_id = (select id from formula.formula_type as ft where ft."name" = 'cable_ffc'));
delete from formula.common_parameter as cp where cp.formula_type_id = (select id from formula.formula_type as ft where ft."name" = 'cable_ffc');
delete from formula.schedule_date as sd where sd.formula_type_id = (select id from formula.formula_type as ft where ft."name" = 'cable_ffc');

