CREATE TABLE IF NOT EXISTS cablewire_connector_item (
	id uuid NOT NULL DEFAULT uuid_generate_v1(),
	connector_item varchar(200) NULL,
	remark varchar(400) NULL,
	create_time timestamptz NOT null default now(),
	disable_time timestamptz null,
	CONSTRAINT cablewire_connector_item_pk PRIMARY KEY (id),
	CONSTRAINT cablewire_connector_item_un UNIQUE (connector_item)
);

CREATE TABLE IF NOT EXISTS cablewire_connector_type (
	id uuid NOT NULL DEFAULT uuid_generate_v1(),
	connector_type varchar(200) NULL,
    connector_item_id uuid not NULL,
    vendor_pn varchar(200) NULL,
    price uuid NOT NULL DEFAULT uuid_generate_v1(),
    process_time uuid NOT NULL DEFAULT uuid_generate_v1(),
	remark varchar(400) NULL,
	create_time timestamptz NOT null default now(),
	disable_time timestamptz null,
	CONSTRAINT cablewire_connector_type_pk PRIMARY KEY (id),
    CONSTRAINT cablewire_connector_type_fk FOREIGN KEY (connector_item_id) REFERENCES cablewire_connector_item(id),
	CONSTRAINT cablewire_connector_type_un UNIQUE (connector_type,connector_item_id,vendor_pn)
);

CREATE TABLE IF NOT EXISTS cablewire_cable_type (
	id uuid NOT NULL DEFAULT uuid_generate_v1(),
	cable_type varchar(200) NULL,
	remark varchar(400) NULL,
	create_time timestamptz NOT null default now(),
	disable_time timestamptz null,
	CONSTRAINT cablewire_cable_type_pk PRIMARY KEY (id),
	CONSTRAINT cablewire_cable_type_un UNIQUE (cable_type)
);

CREATE TABLE IF NOT EXISTS cablewire_cable_gauge (
	id uuid NOT NULL DEFAULT uuid_generate_v1(),
	gauge int not NULL,
	remark varchar(400) NULL,
	create_time timestamptz NOT null default now(),
	disable_time timestamptz null,
	CONSTRAINT cablewire_cable_gauge_pk PRIMARY KEY (id),
	CONSTRAINT cablewire_cable_gauge_un UNIQUE (gauge)
);


CREATE TABLE IF NOT EXISTS cablewire_cable_type_gauge (
	id uuid NOT NULL DEFAULT uuid_generate_v1(),
	cable_id uuid not NULL,
    gauge_id uuid not NULL,
	remark varchar(400) NULL,
	create_time timestamptz NOT null default now(),
	disable_time timestamptz null,
	CONSTRAINT cablewire_cable_type_gauge_pk PRIMARY KEY (id),
    CONSTRAINT cablewire_cable_type_gauge_fk FOREIGN KEY (cable_id) REFERENCES cablewire_cable_type(id),
    CONSTRAINT cablewire_cable_type_gauge_fk1 FOREIGN KEY (gauge_id) REFERENCES cablewire_cable_gauge(id),
	CONSTRAINT cablewire_cable_type_gauge_un UNIQUE (cable_id,gauge_id)
);
