drop table if exists turning_nylok;
drop table if exists turning_nylok_color;
drop table if exists turning_nylok_diameter;
drop table if exists turning_nylok_length;
drop table if exists turning_plating;
drop table if exists turning_screw_diameter;
drop table if exists turning_heat_treatment;
drop table if exists turning_material;

CREATE TABLE IF NOT EXISTS turning_material (
	id uuid NOT NULL DEFAULT uuid_generate_v1(),
	part_category_2_id uuid not null,
	material_name varchar(200) not null,
    density numeric not null,
	remark varchar(400) NULL,
	create_time timestamptz NOT null default now(),
	disable_time timestamptz null,
	CONSTRAINT turning_material_pk PRIMARY KEY (id),
	CONSTRAINT turning_material_fk FOREIGN KEY (part_category_2_id) REFERENCES part_category_2(id),
	CONSTRAINT turning_material_un UNIQUE (part_category_2_id, material_name)
);

CREATE TABLE IF NOT EXISTS turning_heat_treatment (
	id uuid NOT NULL DEFAULT uuid_generate_v1(),
	turning_material_id uuid not null,
    remark varchar(400) NULL,
	create_time timestamptz NOT null default now(),
	disable_time timestamptz null,
	CONSTRAINT turning_heat_treatment_pk PRIMARY KEY (id),
    CONSTRAINT turning_heat_treatment_fk FOREIGN KEY (turning_material_id) REFERENCES turning_material(id),
	CONSTRAINT turning_heat_treatment_un UNIQUE (turning_material_id)
);

CREATE TABLE IF NOT EXISTS turning_screw_diameter (
	id uuid NOT NULL DEFAULT uuid_generate_v1(),
	outter_diameter numeric not null,
	inner_diameter numeric not null,
	remark varchar(400) NULL,
	create_time timestamptz NOT null default now(),
	disable_time timestamptz null,
	CONSTRAINT turning_screw_diameter_pk PRIMARY KEY (id),
	CONSTRAINT turning_screw_diameter_un UNIQUE (outter_diameter)
);

CREATE TABLE IF NOT EXISTS turning_plating (
	id uuid NOT NULL DEFAULT uuid_generate_v1(),
	plating_name varchar(200) not null,
	remark varchar(400) NULL,
	create_time timestamptz NOT null default now(),
	disable_time timestamptz null,
	CONSTRAINT turning_plating_pk PRIMARY KEY (id),
	CONSTRAINT turning_plating_un UNIQUE (plating_name)
);

CREATE TABLE IF NOT EXISTS turning_nylok_color (
	id uuid NOT NULL DEFAULT uuid_generate_v1(),
	color_name varchar(200) not null,
	remark varchar(400) NULL,
	create_time timestamptz NOT null default now(),
	disable_time timestamptz null,
	CONSTRAINT turning_nylok_color_pk PRIMARY KEY (id),
	CONSTRAINT turning_nylok_color_un UNIQUE (color_name)
);

CREATE TABLE IF NOT EXISTS turning_nylok_diameter (
	id uuid NOT NULL DEFAULT uuid_generate_v1(),
	diameter_begin numeric not null,
	diameter_end numeric not null,
	remark varchar(400) NULL,
	create_time timestamptz NOT null default now(),
	disable_time timestamptz null,
	CONSTRAINT turning_nylok_diameter_pk PRIMARY KEY (id),
	CONSTRAINT turning_nylok_diameter_un UNIQUE (diameter_begin, diameter_end)
);

CREATE TABLE IF NOT EXISTS turning_nylok_length (
	id uuid NOT NULL DEFAULT uuid_generate_v1(),
	length_begin numeric not null,
	length_end numeric not null,
	remark varchar(400) NULL,
	create_time timestamptz NOT null default now(),
	disable_time timestamptz null,
	CONSTRAINT turning_nylok_length_pk PRIMARY KEY (id),
	CONSTRAINT turning_nylok_length_un UNIQUE (length_begin, length_end)
);

CREATE TABLE IF NOT EXISTS turning_nylok (
	id uuid NOT NULL DEFAULT uuid_generate_v1(),
	material_id uuid not null,
	color_id uuid not null,
	diameter_id uuid not null,
	length_id uuid not null,
	remark varchar(400) NULL,
	create_time timestamptz NOT null default now(),
	disable_time timestamptz null,
	CONSTRAINT turning_nylok_pk PRIMARY KEY (id),
	CONSTRAINT turning_nylok_un UNIQUE (material_id, color_id, diameter_id, length_id),
	CONSTRAINT turning_nylok_fk FOREIGN KEY (material_id) REFERENCES turning_material(id),
	CONSTRAINT turning_nylok_fk1 FOREIGN KEY (color_id) REFERENCES turning_nylok_color(id),
	CONSTRAINT turning_nylok_fk2 FOREIGN KEY (diameter_id) REFERENCES turning_nylok_diameter(id),
	CONSTRAINT turning_nylok_fk3 FOREIGN KEY (length_id) REFERENCES turning_nylok_length(id)
);


